const isOneTrustLoaded=()=>{if(!window.OptanonActiveGroups||!window.Cookies)return;const oneTrustCookies=window.Cookies.get('OptanonConsent');const oneTrustIsLoaded=window.Optanon&&window.OptanonActiveGroups.indexOf('C')!==-1;return oneTrustCookies&&oneTrustIsLoaded;};const handleBlockedCookies=(blockedCookies,type)=>{blockedCookies.map((blockedCookie)=>{if(window.Cookies.get(blockedCookie.name)){console.log(`Due to Cookie Consent deleting cookie ${type}: ${blockedCookie.name} `);Cookies.remove(blockedCookie.name,{path:'/',domain:window.location.hostname});Cookies.remove(blockedCookie.name,{path:'/',domain:'.allaboutcookies.org'});Cookies.remove(blockedCookie.name,{path:'/',domain:blockedCookie.host});}});};const getOptoutCookiesTypes=()=>window.Optanon.GetDomainData().Groups.filter((group)=>!window.OptanonActiveGroups.includes(group.OptanonGroupId));const generateOTTag=()=>{const optOutIds=getOptoutCookiesTypes().map((cookieType)=>cookieType.OptanonGroupId).join(',');if(!optOutIds)return;const OTTag=document.createElement('script');OTTag.setAttribute('src','https://cdn.cookielaw.org/opt-out/otCCPAiab.js');OTTag.setAttribute('type','text/javascript');OTTag.setAttribute('charset','UTF-8');OTTag.setAttribute('ccpa-opt-out-ids',optOutIds);OTTag.setAttribute('ccpa-opt-out-geo','all');OTTag.setAttribute('ccpa-opt-out-lspa','true');setTimeout(()=>document.body.appendChild(OTTag));};const handleChangesAllowingCookies=()=>{getOptoutCookiesTypes().forEach((group)=>{const cookiesToBeDeleted=group.Cookies.map((cookies)=>({name:cookies.Name,host:Cookies.Host,}));handleBlockedCookies(cookiesToBeDeleted,group.GroupName);});generateOTTag();};const getOptanonWindowService=setInterval(()=>{if(isOneTrustLoaded()){window.Optanon.OnConsentChanged(()=>{handleChangesAllowingCookies();});clearInterval(getOptanonWindowService);handleChangesAllowingCookies();}},100);window.privacySettings={getUserDisallowedTargetingCookies:'',};;