const bodyElement=document.querySelector('body');const menuMobileElement=document.querySelector('#menu-mobile');const closeButtonElements=document.querySelectorAll('#close-button');const showMenuMobile=()=>{menuMobileElement.classList.remove('hidden');setTimeout(()=>{menuMobileElement.classList.add('left-0');menuMobileElement.classList.remove('left-[999px]');bodyElement.classList.add('overflow-hidden');[...closeButtonElements].map((closeButtonElement)=>{closeButtonElement.classList.remove('rotate-45');closeButtonElement.classList.add('rotate-[135deg]');});});};const closeMenuMobile=()=>{menuMobileElement.classList.add('left-[999px]');menuMobileElement.classList.remove('left-0');bodyElement.classList.remove('overflow-hidden');[...closeButtonElements].map((closeButtonElement)=>{closeButtonElement.classList.remove('rotate-[135deg]');closeButtonElement.classList.add('rotate-45');});setTimeout(()=>{menuMobileElement.classList.add('hidden');},500);};const handleHideMenuOnTranslatedPages=()=>{if(window.Cookies.get('translation')){document.querySelector('#header-navigation-container').classList.add('hidden');}};handleHideMenuOnTranslatedPages();;if(typeof bodyElement!=='undefined'){const bodyElement=document.querySelector('body');}
const bodyOverlayElement=document.querySelector('#search-body-overlay');const searchElement=document.querySelector('#search-box');const searchInputElement=document.querySelector('#swiftypeSearchBox');const searchCloseButtonElement=document.querySelector('#search-close-button');const showSearchBox=()=>{searchElement.classList.remove('hidden');searchInputElement.focus();setTimeout(()=>{searchElement.classList.add('top-0');searchElement.classList.remove('top-[-999px]');bodyElement.classList.add('overflow-hidden');bodyOverlayElement.classList.add('bg-black/[.35]','min-h-screen','inset-0');searchCloseButtonElement.classList.remove('rotate-45');searchCloseButtonElement.classList.add('rotate-[135deg]');});};const closeSearchBox=()=>{searchElement.classList.add('top-[-999px]');searchElement.classList.remove('top-0');bodyElement.classList.remove('overflow-hidden');bodyOverlayElement.classList.remove('bg-black/[.35]','min-h-screen','inset-0');searchCloseButtonElement.classList.remove('rotate-[135deg]');searchCloseButtonElement.classList.add('rotate-45');setTimeout(()=>{searchElement.classList.add('hidden');},500);};const handleHideSearchOnTranslatedPages=()=>{if(window.Cookies.get('translation')){document.querySelector('#swiftypeDesktop').classList.add('hidden');document.querySelector('#swiftypeMobile').classList.add('hidden');}};const handleEscKeyPress=()=>{document.addEventListener('keydown',({key})=>{const stAutocompleteElement=document.querySelector('[data-st-target-element="#swiftypeSearchBox"]');const stSearchOverlayElement=document.querySelector('.st-search-chrome-overlay-output');if(key==='Escape'){if(stAutocompleteElement.offsetParent===null&&stSearchOverlayElement.offsetParent===null)closeSearchBox();}});};handleEscKeyPress();handleHideSearchOnTranslatedPages();;